#!/bin/shecho network name:read network echo Input user id:read userid echo "Please enter your password";read -s password echo go i=$(wpa_cli add_network)i=${i:27:1}echo "working with $i"echo $iwpa_cli set_network $i ssid "$network"wpa_cli set_network $i scan_ssid 1wpa_cli set_network $i proto WPA2wpa_cli set_network $i priority 1wpa_cli set_network $i key_mgmt WPA-EAPwpa_cli set_network $i eap PEAPwpa_cli set_network $i pairwise CCMPwpa_cli set_network $i group CCMPwpa_cli set_network $i identity "$userid"wpa_cli set_network $i password "$password"wpa_cli set_network $i phase2 "auth=MSCHAPV2" echo end